router.get('/working-hours', authMiddleware, async (req, res) => {
  if (req.user.userType !== 'doctor') {
    return res.status(403).json({ message: 'Access denied. Not a doctor.' });
  }
  try {
    const doctor = await Doctor.findById(req.user.userId);
    if (!doctor) {
