import { describe, it, expect, beforeAll, afterAll, afterEach, vi } from 'vitest';
import '../routes/prescriptions';

const request = require('supertest');
const express = require('express');
const mongoose = require('mongoose');
const { MongoMemoryServer } = require('mongodb-memory-server');

const Module = require('module');
const originalRequire = Module.prototype.require;

const sendEmailMock = vi.fn().mockResolvedValue(true);

const mockAuth = (req, res, next) => {
    if (req.headers['mock-user']) {
        req.user = JSON.parse(req.headers['mock-user']);
        next();
    } else {
        res.status(401).json({ message: 'No token, authorization denied' });
    }
};

class MockPDFDocument {
    constructor() {
        this._texts = [];
        this._res = null;
    }
    pipe(res) {
        this._res = res;
        return this;
    }
    text(content, ...args) {
        try { this._texts.push(String(content)); } catch (e) { this._texts.push(''); }
        return this;
    }
    fontSize() { return this; }
    fillColor() { return this; }
    font() { return this; }
    strokeColor() { return this; }
    lineWidth() { return this; }
    moveTo() { return this; }
    lineTo() { return this; }
    stroke() { return this; }
    moveDown() { return this; }
    end() {
        const payload = Buffer.from(JSON.stringify({ texts: this._texts }));
        if (this._res && typeof this._res.setHeader === 'function') {
            this._res.setHeader('content-type', 'application/pdf');
            this._res.setHeader('content-disposition', 'attachment; filename=prescription.pdf');
        }
        if (this._res && typeof this._res.write === 'function') {
            this._res.write(payload);
            this._res.end();
        }
    }
}

Module.prototype.require = function (id) {
    if (id === 'pdfkit') {
        return MockPDFDocument;
    }
    if (id === '../middleware/auth') {
        return mockAuth;
    }
    if (id === '../utils/email_utils') {
        return sendEmailMock;
    }
    return originalRequire.apply(this, arguments);
};

const Appointment = require('../models/Appointment');
const MedicalRecord = require('../models/MedicalRecord');
const Patient = require('../models/Patient');
const Doctor = require('../models/Doctor');

const buildAppointmentData = (overrides = {}) => ({
    phoneNumber: '9999999999',
    email: 'appointment@example.com',
    birthDate: new Date('1990-01-01'),
    sex: 'other',
    primaryLanguage: 'English',
    symptomsBegin: '2023-01-01',
    paymentStatus: 'pending',
    ...overrides
});

if (!Appointment.schema.statics.__defaultsApplied) {
    Appointment.schema.pre('validate', function (next) {
        const defaults = buildAppointmentData();
        Object.entries(defaults).forEach(([field, value]) => {
            if (this[field] === undefined || this[field] === null) {
                this[field] = value;
            }
        });
        next();
    });
    Appointment.schema.statics.__defaultsApplied = true;
}

const createAppointment = (data = {}) => Appointment.create(buildAppointmentData(data));
const createAppointments = (list = []) => Appointment.create(list.map(buildAppointmentData));
const insertAppointmentRaw = (data = {}) => Appointment.collection.insertOne(buildAppointmentData(data));

let mongoServer;
let app;

beforeAll(async () => {
    mongoServer = await MongoMemoryServer.create();
    const uri = mongoServer.getUri();
    await mongoose.connect(uri);

    app = express();
    app.use(express.json());
    app.use('/api/prescriptions', require('../routes/prescriptions'));
}, 120000);

afterAll(async () => {
    await mongoose.disconnect();
    await mongoServer.stop();
});

afterEach(async () => {
    await Appointment.deleteMany({});
    await MedicalRecord.deleteMany({});
    await Patient.deleteMany({});
    await Doctor.deleteMany({});
    vi.clearAllMocks();
});

const createMockUserHeader = (userId, userType) => {
    return JSON.stringify({ userId, userType });
};

describe('POST /api/prescriptions', () => {
    it('should create a new prescription successfully and queue email', async () => {
        const patient = await Patient.create({
            fullName: 'John Doe',
            email: 'patient@test.com',
            password: 'Test@1234',
        });

        const doctor = await Doctor.create({
            fullName: 'Dr. Smith',
            email: 'doctor@test.com',
            password: 'Test@1234',
            specialization: 'Cardiology',
            experience: 10,
            consultationFee: 500,
            licenseNumber: 'LIC-POST-1',
            address: '123 Medical St',
        });

        const appointment = await createAppointment({
            patient: patient._id,
            doctor: doctor._id,
            patientNameForVisit: 'John Doe',
            consultationFeeAtBooking: 500,
            date: new Date('2024-12-25'),
            time: '10:00 AM',
            primaryReason: 'Chest pain',
            status: 'completed',
        });

        const mockUser = createMockUserHeader(doctor._id.toString(), 'doctor');

