describe('POST /api/schedule/blocked-times', () => {
    it('should add blocked time successfully', async () => {
        const doctor = await Doctor.create({
            fullName: 'Dr. Test',
            email: 'doctor@test.com',
            password: 'Test@1234',
            specialization: 'Cardiology',
            experience: 10,
            qualifications: ['MBBS', 'MD'],
